import React, { useState } from "react";
import "./Requests.css";

// بيانات تجريبية
const demoRequests = [
  {
    id: 1,
    patientName: "أحمد محمد",
    location: "مدينة نصر",
    phone: "01123456789",
    services: ["قياس الضغط", "حقن"],
    status: "جديد"
  },
  {
    id: 2,
    patientName: "مها علي",
    location: "المعادي",
    phone: "01234567890",
    services: ["تعليق محاليل", "تركيب كانيولا"],
    status: "جديد"
  }
];

export default function Requests() {
  const [requests, setRequests] = useState(demoRequests);

  function handleAccept(id) {
    setRequests(requests.map(req => req.id === id ? { ...req, status: "مقبول" } : req));
  }
  function handleReject(id) {
    setRequests(requests.map(req => req.id === id ? { ...req, status: "مرفوض" } : req));
  }

  return (
    <div className="requests-main">
      <h2>الطلبات الجديدة</h2>
      {requests.length === 0 && <div className="empty">لا يوجد طلبات حالياً.</div>}
      {requests.map(req => (
        <div className="request-card" key={req.id}>
          <div><b>اسم المريض:</b> {req.patientName}</div>
          <div><b>العنوان:</b> {req.location}</div>
          <div><b>رقم الهاتف:</b> {req.phone}</div>
          <div><b>الخدمات المطلوبة:</b> {req.services.join(", ")}</div>
          <div><b>الحالة:</b> <span className={"status-"+req.status}>{req.status}</span></div>
          {req.status === "جديد" && (
            <div className="req-actions">
              <button onClick={() => handleAccept(req.id)} className="accept-btn">قبول</button>
              <button onClick={() => handleReject(req.id)} className="reject-btn">رفض</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
